namespace Magestore\Pdfinvoiceplus\Controller\Adminhtml\Index;

/**
 * class ExportExcel
 *
 * @category Magestore
 * @package  Magestore_Pdfinvoiceplus
 * @module   Pdfinvoiceplus
 * @author   Magestore Developer
 */
class ExportExcel extends \Magestore\Pdfinvoiceplus\Controller\Adminhtml\AbstractExportAction
{
    protected $_exportFileName = 'pdf_template.xml';

    /**
     * get content.
     *
     * @return string
     */
    protected function _getContent()
    {
        return $this->_getGridExportBlock()->getExcelFile();
    }
}