namespace Magestore\Pdfinvoiceplus\Model\Variables\Option\BarcodeFilename;

/**
 * class Quote
 *
 * @category Magestore
 * @package  Magestore_Pdfinvoiceplus
 * @module   Pdfinvoiceplus
 * @author   Magestore Developer
 */
class Quote implements \Magento\Framework\Option\ArrayInterface
{

    /**
     *
     */
    const QUOTE_ENTITY_ID = '{{var quote_entity_id}}';
    /**
     *
     */
    const QUOTE_CREATED_AT = '{{var quote_created_at}}';


    /**
     * Return array of options as value-label pairs
     *
     * @return array Format: array(array('value' => '<value>', 'label' => '<label>'), ...)
     */
    public function toOptionArray()
    {
        return [
            ['value' => self::QUOTE_ENTITY_ID, 'label' => 'Entity Id'],
            ['value' => self::QUOTE_CREATED_AT, 'label' => 'Created At']
        ];
    }
}